package com.hz.authority.management.sys.entity.authority;import com.hz.authority.management.sys.entity.Role;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.ArrayList;import java.util.Collection;import java.util.List;/** * @author yaohw * @date 2019-09-25 16:12 */@Data@Builder@NoArgsConstructor@AllArgsConstructorpublic class SysUserDetails implements UserDetails {    private Integer id;    private String username;    private String password;    private Role role;    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        List<GrantedAuthority> authorities = new ArrayList<>();        authorities.add(new SimpleGrantedAuthority(role.getName()));        return authorities;    }    @Override    public String getPassword() {        return this.password;    }    @Override    public String getUsername() {        return this.username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }}